class App extends React.Component {
  constructor() {
    super();
    this.state = {
      count: 0,
      Values: [],
      textvalue: "",
    };
    //this.Values = [];
    //this.textvalue = "text";
    this.Addvalue = this.Addvalue.bind(this);
    //const [textvalues, setState] = useState("");
  }

  increase(prop) {
    let floors = [...this.state.Values];

    // Add item to it
    floors.push({ prop });

    // Set state
    this.setState({ floors });
    //this.setState({ Values: Values.push(prop) });
  }

  Addvalue(evt) {
    //console.log(this.state.textvalue);
    evt.preventDefault();

    let tasks = this.state.Values;
    let textvalue = this.state.textvalue;
    tasks.push(textvalue);
    this.setState({ textvalue: "" });
    this.setState({ Values: tasks });
    //console.log(this.state.Values);
  }
  updateInput = (evt) => {
    this.setState({ textvalue: evt.target.value });
  };
  handleKeypress = (e) => {
    //it triggers by pressing the enter key
    if (e.keyCode === 13) {
      this.Addvalue(e);
    }
  };
  render() {
    console.log(this.state.Values);
    let vals = this.state.Values;
    console.log(typeof vals);
    return (
      <div style={{ margin: "50px" }}>
        <Stack direction="row" justifyContent="center">
          <Box
            component="div"
            sx={{
              "& > :not(style)": {
                m: 1,
                width: "50ch",
              },
              display: "contents",
            }}
            noValidate
            autoComplete="off"
          >
            <TextField
              id="standard-basic"
              label="Standard"
              variant="standard"
              value={this.state.textvalue}
              onChange={this.updateInput}
              onKeyPress={this.handleKeypress}
              sx={{
                width: "80%",
              }}
            />

            <Button
              variant="contained"
              sx={{
                width: "92px !important",
              }}
              startIcon={<AddCircle />}
              onClick={this.Addvalue}
            >
              Add
            </Button>
          </Box>
        </Stack>
        <Stack direction="row" justifyContent="center">
          <Box
            component="div"
            sx={{
              "& > :not(style)": {
                m: 1,
                width: "50ch",
              },
              display: "contents",
            }}
            noValidate
            autoComplete="off"
          >
            <List
              sx={{
                width: "100%",
                maxWidth: 500,
                bgcolor: "background.paper",
                position: "relative",
                overflow: "auto",
                maxHeight: 300,
                "& ul": { padding: 0 },
              }}
              subheader={<li />}
            >
              {this.state.Values.length !== 0 ? (
                vals.map((Va) => (
                  <li key={`section-${Va}`}>
                    <ul>
                      <ListSubheader>{` ${Va}`}</ListSubheader>
                      {/* {[this.state.Values].map((item) => ( */}
                      {/* <ListItem key={`item-${Va}`}>
                        <ListItemText primary={`${Va}`} />
                      </ListItem> */}
                      {/* ))} */}
                    </ul>
                  </li>
                ))
              ) : (
                <ListSubheader>No Data Available..</ListSubheader>
              )}
            </List>
          </Box>
        </Stack>
      </div>
    );
  }
}

export default App;
