//import axios from "axios";
import React from "react";
import Box from "@mui/material/Box";
// import { ListItem, List, ListSubheader, ListItemText } from "@mui/material";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import AddCircle from "@mui/icons-material/AddCircle";
// import DeleteCircle from "@mui/icons-material/DeleteCircle";
import DeleteIcon from "@mui/icons-material/Delete";
import Stack from "@mui/material/Stack";
import Input from "@mui/material/Input";
const ariaLabel = { "aria-label": "description" };

//import Post from "./Post";
//const baseURL = "http://localhost:8081";

// export default function App() {
//   // const [post, setPost] = React.useState(null);

//   // React.useEffect(() => {
//   //   // axios
//   //   //   .post(`http://localhost:8081/user/generateToken`, { user: "withinloop" })
//   //   //   .then((res) => {
//   //   //     console.log(res);
//   //   //     console.log(res.data);
//   //   //     setPost(response.data);
//   //   //   });
//   //   axios.get(baseURL).then((response) => {
//   //     setPost(response.data);
//   //   });
//   // }, []);

//   //if (!post) return null;

//   // console.log(post);
//   // if (!post) return "No post!";
//   return (
//     <Stack direction="row" justifyContent="center">
//       <Box
//         component="div"
//         sx={{
//           "& > :not(style)": { m: 1, width: "25ch" },
//         }}
//         noValidate
//         autoComplete="off"
//       >
//         <TextField
//           id="filled-basic"
//           label="Filled"
//           variant="filled"
//           sx={{
//             width: "80%",
//           }}
//         />
//         <Button
//           variant="contained"
//           sx={{
//             width: "20px",
//           }}
//           endIcon={<SendIcon />}
//         >
//           Send
//         </Button>
//       </Box>
//     </Stack>
//   );
// }
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      count: 0,
      showHide: false,
      Values:
        localStorage.getItem("Values") === null
          ? []
          : typeof localStorage.getItem("Values") !== "string"
          ? localStorage.getItem("Values")
          : localStorage.getItem("Values").split(","),
      textvalue: "",
      textsubvalue: "",
    };
    //this.Values = [];
    //this.textvalue = "text";
    this.Addvalue = this.Addvalue.bind(this);
    //const [textvalues, setState] = useState("");

    //console.log(itemList);
  }

  increase(prop) {
    let floors = [...this.state.Values];

    // Add item to it
    floors.push({ prop });

    // Set state
    this.setState({ floors });
    //this.setState({ Values: Values.push(prop) });
  }

  Item = (props) => {
    const { sx, ...other } = props;
    console.log(props);
    if (
      props.children !== "No information" &&
      props.children !== "" &&
      props.children !== null
    ) {
      return (
        <Box
          sx={{
            mt: 2,
            bgcolor: (theme) =>
              theme.palette.mode === "dark" ? "#101010" : "#fff",
            color: (theme) =>
              theme.palette.mode === "dark" ? "grey.300" : "grey.800",
            border: "1px solid",
            borderColor: (theme) =>
              theme.palette.mode === "dark" ? "grey.800" : "grey.300",
            p: 1,
            m: 1,
            "&:hover": {
              backgroundColor: "primary.dark",
              opacity: [0.9, 0.8, 0.7],
            },
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderRadius: 2,
            fontSize: "0.875rem",
            fontWeight: "700",
            ...sx,
          }}
          {...other}
        >
          <label>{props.children}</label>

          <Button
            variant="contained"
            sx={{
              width: "25px !important",
            }}
            startIcon={<DeleteIcon sx={{ marginLeft: "8px" }} />}
            onClick={() => this.handleDelete(props.children)}
          ></Button>
        </Box>
      );
    } else {
      return (
        <Box
          sx={{
            mt: 2,
            bgcolor: (theme) =>
              theme.palette.mode === "dark" ? "#101010" : "#fff",
            color: (theme) =>
              theme.palette.mode === "dark" ? "grey.300" : "grey.800",
            border: "1px solid",
            borderColor: (theme) =>
              theme.palette.mode === "dark" ? "grey.800" : "grey.300",
            p: 1,
            m: 1,
            "&:hover": {
              backgroundColor: "primary.dark",
              opacity: [0.9, 0.8, 0.7],
            },
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderRadius: 2,
            fontSize: "0.875rem",
            fontWeight: "700",
            ...sx,
          }}
          {...other}
        >
          <label>
            {props.children === "No information"
              ? props.children
              : "No information"}
          </label>
        </Box>
      );
    }
  };
  handleDelete(e) {
    console.log(e);
    const listval =
      typeof localStorage.getItem("Values") !== "string"
        ? localStorage.getItem("Values")
        : localStorage.getItem("Values").split(",");
    const newList = listval.filter((item) => item !== e);
    localStorage.setItem("Values", newList);
    // setState("Values") = newList;
    this.setState({ Values: newList });
    console.log(newList);
    //setList(newList);
  }
  Addvalue(evt) {
    //console.log(this.state.textvalue);
    if (this.state.textvalue.length > 0) {
      evt.preventDefault();
      if (localStorage.getItem("Values") === null) {
        localStorage.setItem("Values", []);
      }
      let tasks = this.state.Values;
      let textvalue = this.state.textvalue;
      tasks.push(textvalue);
      localStorage.setItem("Values", tasks);
      this.setState({ textvalue: "" });
      this.setState({ Values: tasks });
    }
    //console.log(this.state.Values);
  }
  updateInput = (evt) => {
    this.setState({ textvalue: evt.target.value });
    if (evt.target.value.length >= 1) {
      this.setState({ showHide: true });
    } else {
      this.setState({ showHide: false });
    }
  };
  handleKeypress = (e) => {
    //it triggers by pressing the enter key
    console.log(e.charCode);
    if (e.charCode === 13 && this.state.textvalue.length > 0) {
      //this.Addvalue(e);
      this.setState({ showHide: true });
    }
  };
  handlesubKeypress = (e) => {
    //it triggers by pressing the enter key
    // console.log(e);
    if (e.charCode === 13 && this.state.textvalue.length > 0) {
      //this.Addvalue(e);
      this.setState({ showHide: true });
    }
  };
  Notesman = (e) => {
    if (this.state.showHide) {
      return (
        <>
          <Stack direction="row" justifyContent="center">
            <Box
              component="div"
              sx={{
                "& > :not(style)": {
                  m: 1,
                  width: "39%",
                },
                display: "contents",
              }}
              noValidate
              autoComplete="off"
            >
              <Input
                placeholder="Enter Your Notice"
                value={this.state.textvalue}
                onChange={this.updateInput}
                onKeyPress={this.handleKeypress}
                inputProps={ariaLabel}
              />
            </Box>
