//import axios from "axios";
import React from "react";
import Box from "@mui/material/Box";
// import { ListItem, List, ListSubheader, ListItemText } from "@mui/material";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import AddCircle from "@mui/icons-material/AddCircle";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
// import DeleteCircle from "@mui/icons-material/DeleteCircle";
import DeleteIcon from "@mui/icons-material/Delete";
import SaveRoundedIcon from "@mui/icons-material/SaveRounded";
import Stack from "@mui/material/Stack";
import Input from "@mui/material/Input";
const ariaLabel = { "aria-label": "description" };

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      showHide: false,
      Values:
        localStorage.getItem("Values") === null
          ? []
          : typeof localStorage.getItem("Values") === "string"
          ? JSON.parse(localStorage.getItem("Values"))
          : localStorage.getItem("Values"),
      textvalue: "",
