//   //   //     setPost(response.data);
//   //   //   });
//   //   axios.get(baseURL).then((response) => {
//   //     setPost(response.data);
//   //   });
//   // }, []);

//   //if (!post) return null;

//   // console.log(post);
//   // if (!post) return "No post!";
//   return (
//     <Stack direction="row" justifyContent="center">
//       <Box
//         component="div"
//         sx={{
//           "& > :not(style)": { m: 1, width: "25ch" },
//         }}
//         noValidate
//         autoComplete="off"
//       >
//         <TextField
//           id="filled-basic"
//           label="Filled"
//           variant="filled"
//           sx={{
//             width: "80%",
//           }}
//         />
//         <Button
//           variant="contained"
//           sx={{
//             width: "20px",
//           }}
//           endIcon={<SendIcon />}
//         >
//           Send
//         </Button>
//       </Box>
//     </Stack>
//   );
// }
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      count: 0,
      showHide: false,
      Values:
        localStorage.getItem("Values") === null
          ? []
          : typeof localStorage.getItem("Values") === "string"
          ? JSON.parse(localStorage.getItem("Values"))
          : localStorage.getItem("Values"),
      textvalue: "",
      textsubvalue: "",
      divshowhide: false,
    };
    //this.Values = [];
    //this.textvalue = "text";
    this.Addvalue = this.Addvalue.bind(this);
    //const [textvalues, setState] = useState("");

    //console.log(itemList);
  }

  increase(prop) {
    let floors = [...this.state.Values];

    // Add item to it
    floors.push({ prop });

    // Set state
    this.setState({ floors });
    //this.setState({ Values: Values.push(prop) });
  }
  Noticeclick(e) {
    alert(e);
    this.setState({ divshowhide: true });
  }
  Item = (props) => {
    const { sx, ...other } = props;
    //console.log(props);
    if (
      props.children !== "No information" &&
      props.children !== "" &&
      props.children !== null
    ) {
      return (
        <Box
          sx={{
            mt: 2,
            bgcolor: (theme) =>
              theme.palette.mode === "dark" ? "#101010" : "#fff",
            color: (theme) =>
              theme.palette.mode === "dark" ? "grey.300" : "grey.800",
            border: "1px solid",
            borderColor: (theme) =>
              theme.palette.mode === "dark" ? "grey.800" : "grey.300",
            p: 1,
            m: 1,
            "&:hover": {
              backgroundColor: "primary.dark",
              opacity: [0.9, 0.8, 0.7],
            },
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderRadius: 2,
            fontSize: "0.875rem",
            fontWeight: "700",
            ...sx,
          }}
          {...other}
          onClick={() => this.Noticeclick(props.children)}
        >
          <label>{props.children[1]}</label>

          <Button
            variant="contained"
            sx={{
              width: "25px !important",
            }}
            startIcon={<DeleteIcon sx={{ marginLeft: "8px" }} />}
